self:RegisterEvent("PARTY_LOOT_METHOD_CHANGED");
self:RegisterEvent("CHAT_MSG_BN_WHISPER");
self:RegisterEvent("GET_ITEM_INFO_RECEIVED");
self:RegisterEvent("UNIT_SPELLCAST_SUCCEEDED");
