Scripts.OnEditFocusLost:
self:HighlightText(0,0);
local value = self:GetText();
if not CEPGP_isNumber(value) then
	CEPGP_print("Base coefficient must contain numbers only", true);
	self:SetText(tostring(COEF));
elseif value == "" then
	CEPGP_print("Base GP coefficient cannot be blank", true);
	self:SetText(tostring(COEF));
elseif tonumber(self:GetText()) == 0 then
	CEPGP_print("Base GP coefficient cannot be 0", true);
	self:SetText(tostring(COEF));
elseif COEF ~= tonumber(value) then
	COEF = tonumber(value);
	CEPGP.GP.Base = tonumber(value);
	CEPGP_print("Updated base coefficient");
end

self:HighlightText(0,0);
local value = self:GetText();
if not CEPGP_isNumber(value) then
	CEPGP_print("Mod coefficient must contain numbers only", true);
	self:SetText(tostring(MOD_COEF));
elseif value == "" then
	CEPGP_print("Mod GP coefficient cannot be blank", true);
	self:SetText(tostring(MOD_COEF));
elseif tonumber(self:GetText()) == 0 then
	CEPGP_print("Mod GP coefficient cannot be 0", true);
	self:SetText(tostring(MOD_COEF));
elseif MOD_COEF ~= tonumber(value) then
	MOD_COEF = tonumber(value);
	CEPGP.GP.Multiplier = tonumber(value);
	CEPGP_print("Updated mod coefficient");
end

self:HighlightText(0,0);
local value = self:GetText();
if not CEPGP_isNumber(value) then
	CEPGP_print("GP modifier must contain numbers only", true);
	self:SetText(tostring(MOD));
elseif value == "" then
	CEPGP_print("GP modifier cannot be blank", true);
	self:SetText(tostring(MOD));
elseif 0 >= tonumber(self:GetText()) then
	CEPGP_print("GP modifier cannot be 0", true);
	self:SetText(tostring(MOD));
elseif MOD ~= tonumber(value) then
	MOD = tonumber(value);
	CEPGP.GP.Mod = tonumber(value);
	CEPGP_print("Updated GP modifier");
end

self:HighlightText(0,0);
local value = self:GetText();
if not CEPGP_isNumber(value) then
	CEPGP_print("Base GP must contain numbers only", true);
	self:SetText(tostring(BASEGP));
elseif value == "" then
	CEPGP_print("Base GP cannot be blank", true);
	self:SetText(tostring(BASEGP));
elseif tonumber(self:GetText()) == 0 then
	CEPGP_print("Base GP cannot be 0", true);
	self:SetText(tostring(BASEGP));
elseif 0 > tonumber(self:GetText()) then
	CEPGP_print("Base GP must be a positive number", true);
	self:SetText(tostring(BASEGP));
elseif BASEGP == tonumber(value) then
	return;
end

BASEGP = tonumber(value);
CEPGP.GP.Min = tonumber(value);
CEPGP_print("Updated base GP");

