	return;
end
if tonumber(value) then
	CEPGP.Loot.GUI.Timer = tonumber(value);
	CEPGP.Loot.GUI.Timer = tonumber(value);
	if value == "0" then
		CEPGP_print("Disabled loot timeout");
	else
		CEPGP_print("Updated loot timeout");
	end
end

