	CEPGP.Loot.RollAnnounce = true;
	CEPGP_print("Rolls will now be announced when players respond for loot.");
else
	CEPGP.Loot.RollAnnounce = false;
	CEPGP_print("Rolls will no longer be announced when players respond for loot.");
end

