	local name, _, _, _, class, _, _, _, online, _, classFileName = GetGuildRosterInfo(i);
	if string.find(name, "-") then
		name = string.sub(name, 0, string.find(name, "-")-1);
	end
	if online then
		CEPGP_groupVersion[i] = {
			[1] = name,
			[2] = "未安装插件",
			[3] = class,
			[4] = classFileName
		};
	else
		CEPGP_groupVersion[i] = {
			[1] = name,
			[2] = "离线",
			[3] = class,
			[4] = classFileName
		};
	end
end
