Scripts.OnEditFocusLost:
self:HighlightText(0,0);
local value;
value = self:GetText();
if CEPGP_keyword == value then
	return;
else
	if value == "" then
		self:SetText(CEPGP_keyword);
		CEPGP_print("你必须指定一个关键字分配战利品", true);
		return;
	else
		CEPGP_keyword = value;
		CEPGP_print("战利品关键字更新");
	end
end

