	TRAFFIC = {};
	CEPGP_UpdateTrafficScrollBar();
	CEPGP_print("已清除EPGP记录");
	CEPGP_traffic_clear = false;
else
	CEPGP_print("您正在试图清除该条EPGP记录. 此操作无法恢复.");
	CEPGP_print("	请再次点击以便清除EPGP记录");
	CEPGP_traffic_clear = true;
end

